mapper.select:
select brd_id, writer, title, content, view_cnt, reg_date, mod_date
	from(
		select brd_id, writer, title, content, view_cnt, reg_date, mod_date,
			row_number() over(order by brd_id desc) as rNum
		from board	
	) brd
	where rNum between #{rowStart} and #{rowEnd}
		order by brd_id desc
